CREATE TABLE sys.dm_audit_actions
(
    action_id varchar(4),
    name nvarchar(128),
    class_desc nvarchar(35),
    covering_action_name nvarchar(128),
    parent_class_desc nvarchar(35),
    covering_parent_action_name nvarchar(128),
    configuration_level nvarchar(128),
    containing_group_name nvarchar(128),
    action_in_log bit NOT NULL
);
